import React, { useState } from 'react';
import bugService from '../services/bugService';

function BugCard({ bug, onUpdate }) {
  const [status, setStatus] = useState(bug.status);
  const [updating, setUpdating] = useState(false);

  const handleStatusChange = async (newStatus) => {
    setUpdating(true);
    try {
      await bugService.updateBug(bug._id, { status: newStatus });
      setStatus(newStatus);
      onUpdate();
    } catch (err) {
      console.error('Failed to update bug:', err);
    } finally {
      setUpdating(false);
    }
  };

  const getSeverityColor = (severity) => {
    switch(severity) {
      case 'high': return '#ff4444';
      case 'medium': return '#ff9800';
      case 'low': return '#4caf50';
      default: return '#999';
    }
  };

  const getStatusLabel = (st) => {
    switch(st) {
      case 'open': return 'Open';
      case 'in-progress': return 'In Progress';
      case 'closed': return 'Closed';
      default: return st;
    }
  };

  return (
    <div className="bug-card" style={{ borderLeft: `4px solid ${getSeverityColor(bug.severity)}` }}>
      <h3>{bug.title}</h3>
      <p className="description">{bug.description}</p>
      
      <div className="bug-meta">
        <span className="severity" style={{ backgroundColor: getSeverityColor(bug.severity) }}>
          {bug.severity.toUpperCase()}
        </span>
        <span className="priority">Priority: {bug.priority}</span>
      </div>

      <div className="status-selector">
        <select value={status} onChange={(e) => handleStatusChange(e.target.value)} disabled={updating}>
          <option value="open">Open</option>
          <option value="in-progress">In Progress</option>
          <option value="closed">Closed</option>
        </select>
      </div>

      {bug.createdAt && (
        <div className="bug-date">
          Created: {new Date(bug.createdAt).toLocaleDateString()}
        </div>
      )}
    </div>
  );
}

export default BugCard;
